import React, { useState, useEffect } from "react";
import axios from "axios";
import Jumbotron from "react-bootstrap/Jumbotron";
import Container from "react-bootstrap/Container";

export default function PokeInfo(props) {
  let [pokemon, setPokemon] = useState({ name: "", types: [], sprites: [], flavor_text_entries: [] });
  let endpoint = "https://pokeapi.co/api/v2";
  let url = `${endpoint}${window.location.pathname}`
  let description = ""
  useEffect(() => {
    console.log(window.location.pathname);
    // get pokemon urls
    axios
      .get(url)
      .then((response) => {
        setPokemon(response.data);
      })
      .catch((error) => {
        console.log(error);
      });

  }, [endpoint, url]);

  useEffect(() => {
    description = pokemon.flavor_text_entries[61].f
    console.log(description)
  }, [pokemon])







  // const types = pokemon.types.map(t => t.type.name).join(" | ")

  return (
    <Jumbotron fluid key={`pokeinfo${pokemon.id}`}>
      <Container>
        <h1>
          #{pokemon.id} {pokemon.name}{" "}
        </h1>
  <p>{}</p>

        {/* <p><strong>Height: </strong>{pokemon.height}</p>
    <p><strong>Weight: </strong>{pokemon.weight}</p> */}

        <img
          src={""}
          alt={pokemon.name}
          style={{ height: "50vh", width: "auto" }}
        ></img>
      </Container>
    </Jumbotron>
  );
}
