import React, { useState, useEffect } from "react";
import axios from "axios";
import Jumbotron from "react-bootstrap/Jumbotron";
import Container from "react-bootstrap/Container";

export default function PokeInfo(props) {
  let [pokemon, setPokemon] = useState({
    name: "",
    flavor_text_entries: [{ flavor_text: "" }],
  });
  let endpoint = "https://pokeapi.co/api/v2";
  let url = `${endpoint}${window.location.pathname}`;

  useEffect(() => {
    console.log(window.location.pathname);
    // get pokemon urls
    axios
      .get(url)
      .then((response) => {
        console.log(response.data);
        return response.data;
      })
      .then((d) => {
        setPokemon({
          name: d.name,
          color: d.color.name,
          generation: d.generation.name,
          is_baby: d.is_baby,
          description: d.flavor_text_entries[4].flavor_text,
          id: d.id,
          category: d.genera[7].genus,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }, [url]);

  useEffect(() => {
    console.log(pokemon);
  }, [pokemon]);

  // const types = pokemon.types.map(t => t.type.name).join(" | ")

  return (
    <Jumbotron fluid key={`pokeinfo${pokemon.id}`}>
      <Container>
        <h1>
          #{pokemon.id} {pokemon.name}{" "}
        </h1>
        <p>{pokemon.description}</p>
        {/* <p><strong>Height: </strong>{pokemon.height}</p> */}
        {/* <p><strong>Weight: </strong>{pokemon.weight}</p> */}
        <img
          src={""}
          alt={pokemon.name}
          style={{ height: "50vh", width: "auto" }}
        ></img>
      </Container>
    </Jumbotron>
  );
}
