import React, { useState, useEffect } from "react";
import axios from "axios";
import Jumbotron from "react-bootstrap/Jumbotron";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

export default function PokeInfo(props) {
  let [pokemon, setPokemon] = useState({
    base_happiness: 70,
    category: "",
    color: "",
    description: "",
    generation: "",
    habitat: "",
    height: 7,
    id: 1,
    image: "",
    is_baby: false,
    name: "",
    types: "grass | poison",
    weight: 69,
  });

  let endpoint = "https://pokeapi.co/api/v2";
  let pagePokeId = window.location.href.substring(
    window.location.href.lastIndexOf("/") + 1
  );

  useEffect(() => {
    let firstData = {};
    // get pokemon urls
    axios
      .get(`${endpoint}/pokemon-species/${pagePokeId}`)
      .then((response) => {
        return response.data;
      })
      .then((d) => {
        firstData = {
          name: d.name,
          color: d.color.name,
          generation: d.generation.name,
          is_baby: d.is_baby,
          description: d.flavor_text_entries[6].flavor_text,
          id: d.id,
          category: d.genera[7].genus,
          habitat: d.habitat.name,
          base_happiness: d.base_happiness,
        };
      })
      .catch((error) => {
        console.log(error);
      });

    // Joins data from /pokemon-species with /pokemon
    axios
      .get(`${endpoint}/pokemon/${pagePokeId}`)
      .then((response) => {
        console.log(response.data);
        return response.data;
      })
      .then((d) => {
        setPokemon({
          ...firstData,
          height: d.height,
          weight: d.weight,
          image: d.sprites.front_default,
          types: d.types.map((t, i) => t.type.name).join(" | "),
        });
      });
  }, [endpoint, pagePokeId]);

  useEffect(() => {
    console.log(pokemon);
  }, [pokemon]);

  // const types = pokemon.types.map(t => t.type.name).join(" | ")

  return (
    <Jumbotron fluid key={`pokeinfo${pokemon.id}`}>
      <Container>
        <Col>
        <h1>
          #{pokemon.id} {pokemon.name}
        </h1>
        <img
          src={pokemon.image}
          alt={pokemon.name}
          style={{ height: "50vh", width: "auto" }}
        ></img>
        </Col>

        <Col>
        </Col>
      </Container>
    </Jumbotron>
  );
}
