import React, { useState, useEffect } from "react";
import axios from "axios";
import Jumbotron from "react-bootstrap/Jumbotron";
import Container from "react-bootstrap/Container";

export default function PokeInfo(props) {
  let [pokemon, setPokemon] = useState({
    name: "",
    flavor_text_entries: [{ flavor_text: "" }],
  });
  let endpoint = "https://pokeapi.co/api/v2";
  let pagePokeId = window.location.href.substring(window.location.href.lastIndexOf("/")+1)

  useEffect(() => {

    // get pokemon urls
    axios
      .get(`${endpoint}/pokemon-species/${pagePokeId}`)
      .then((response) => {
        return response.data;
      })
      .then((d) => {
        setPokemon({
          name: d.name,
          color: d.color.name,
          generation: d.generation.name,
          is_baby: d.is_baby,
          description: d.flavor_text_entries[6].flavor_text,
          id: d.id,
          category: d.genera[7].genus,
          habitat: d.habitat,
          base_happiness: d.base_happiness
        });
      })
      .catch((error) => {
        console.log(error);
      });

  }, [endpoint, pagePokeId]);

  useEffect(() => {
    axios.get(`${endpoint}/pokemon/${pagePokeId}`)
    .then(response => {
      return response.data
    })
    .then(d => {
      console.log({...pokemon,
      })
    })
  }, [pokemon])

  useEffect(() => {
    console.log(pokemon);
  }, [endpoint, pagePokeId, pokemon]);

  // const types = pokemon.types.map(t => t.type.name).join(" | ")

  return (
    <Jumbotron fluid key={`pokeinfo${pokemon.id}`}>
      <Container>
        <h1>
          #{pokemon.id} {pokemon.name}{" "}
        </h1>
        <p>{pokemon.description}</p>
        {/* <p><strong>Height: </strong>{pokemon.height}</p> */}
        {/* <p><strong>Weight: </strong>{pokemon.weight}</p> */}
        <img
          src={""}
          alt={pokemon.name}
          style={{ height: "50vh", width: "auto" }}
        ></img>
      </Container>
    </Jumbotron>
  );
}
